mapper.select:
select * from (select ROW_NUMBER() over (order by tax_Num ) as rn,
from taxes
tax_Num like '%'||#{searchWord}||'%' or tax_Payment_Status like '%'||#{searchWord}||'%'
) where user_num=#{userNum} and rn BETWEEN #{startRow} and #{endRow}

select 	
 from taxes
where tax_NUM = #{taxNum} and user_num=#{userNum}

mapper.update:
update taxes
set income_Tax=#{incomeTax }, vat_Tax=#{vatTax },tax_Deduction=#{taxDeduction },tax_Due_Date=#{taxDueDate}
,tax_Payment_Status=#{taxPaymentStatus}
where tax_NUM = #{taxNum} and user_num=#{userNum}
